# Comprueba los servicios inicialesdocker container exec $env:mssql_container_name powershell.exe /C "Get-Service *SQL* | select-object status, name, displayName, StartType"# Para y deshabilita los que no interesendocker container exec $env:mssql_container_name powershell.exe /C "Stop-Service SQLTELEMETRY"docker container exec $env:mssql_container_name powershell.exe /C "Set-Service SQLTELEMETRY -StartupType Disabled"# Habilita e inicia el SQLServerAgentdocker container exec $env:mssql_container_name powershell.exe /C "Set-Service sqlserveragent -StartupType Automatic"docker container exec $env:mssql_container_name powershell.exe /C "Start-Service sqlserveragent"docker container exec $env:mssql_container_name powershell.exe /C "Start-Service SQLWriter"docker container exec $env:mssql_container_name powershell.exe /C "Set-Service SQLWriter -StartupType Automatic"# Resumen de los servicios después de la configuracióndocker container exec $env:mssql_container_name powershell.exe /C "Get-Service *SQL* | select-object status, name, displayName, StartType"